                      {salaryReports.reduce((sum, report) => sum + parseFloat(report.eidBonus || 0), 0).toFixed(2)}
                    </td>
                    <td className="p-2 sm:p-3">الإجمالي</td>
                    <td className="p-2 sm:p-3"></td>
                    <td className="p-2 sm:p-3"></td>
                  </tr>
                </tbody>
              </table>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default MonthlySalaryReport;
