import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../components/AuthProvider';
import NavBar from '../components/NavBar';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import { DateTime } from 'luxon';
import * as XLSX from 'xlsx';
import { Document, Packer, Paragraph, Table, TableCell, TableRow, WidthType, TextRun } from 'docx';
import { saveAs } from 'file-saver';

const MonthlySalaryReport = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  const [searchCode, setSearchCode] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [salaryReports, setSalaryReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editingReport, setEditingReport] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [error, setError] = useState('');
  const [visibleColumns, setVisibleColumns] = useState({
    code: true,
    fullName: true,
    department: true,
    baseSalary: true,
    medicalInsurance: true,
    socialInsurance: true,
    mealAllowance: true,
    eidBonus: true,
    totalWorkHours: true,
    totalWorkDays: true,
    totalAbsenceDays: true,
    lateDeductionDays: true,
    totalDeductions: true,
    deductionsValue: true,
    totalOvertime: true,
    overtimeValue: true,
    totalWeeklyLeaveDays: true,
    totalAnnualLeaveDays: true,
    totalMedicalLeaveDays: true,
    totalAnnualLeaveYear: true,
    penaltiesValue: true,
    violationsInstallment: true,
    totalViolationsValue: true,
    netSalary: true,
    officialLeaveDays: true,
    leaveCompensation: true,
    advancePayment: true,
    remainingViolationsBalance: true,
  });

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleSearch = async () => {
    if (!dateFrom || !dateTo) {
      setError('يرجى إدخال تاريخ البداية وتاريخ النهاية');
      return;
    }

    const startDate = DateTime.fromISO(dateFrom, { zone: 'Africa/Cairo' });
    const endDate = DateTime.fromISO(dateTo, { zone: 'Africa/Cairo' });

    if (!startDate.isValid || !endDate.isValid) {
      setError('تاريخ البداية أو النهاية غير صالح');
      return;
    }

    if (startDate > endDate) {
      setError('تاريخ البداية يجب أن يكون قبل تاريخ النهاية');
      return;
    }

    setError('');
    setLoading(true);
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/fingerprints/salary-report`,
        {
          params: { code: searchCode, dateFrom, dateTo },
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      const normalizedReports = res.data.salaryReports.map((report) => normalizeDays(report));
      setSalaryReports(normalizedReports);
    } catch (err) {
      console.error('Error fetching salary reports:', err.response?.data?.message || err.message);
      setError(`خطأ أثناء البحث: ${err.response?.data?.message || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleShowAll = async () => {
    setLoading(true);
    setError('');
    try {
      const now = DateTime.local().setZone('Africa/Cairo');
      const defaultDateFrom = now.startOf('month').toISODate();
      const defaultDateTo = now.endOf('month').toISODate();

      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/fingerprints/salary-report`,
        {
          params: { code: '', dateFrom: defaultDateFrom, dateTo: defaultDateTo },
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      const normalizedReports = res.data.salaryReports.map((report) => normalizeDays(report));
      setSalaryReports(normalizedReports);
      setSearchCode('');
      setDateFrom('');
      setDateTo('');
    } catch (err) {
      console.error('Error fetching all salary reports:', err.response?.data?.message || err.message);
      setError(`خطأ أثناء جلب جميع التقارير: ${err.response?.data?.message || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const normalizeDays = (report) => {
    const totalDays =
      (parseInt(report.totalWorkDays, 10) || 0) +
      (parseInt(report.totalAbsenceDays, 10) || 0) +
      (parseInt(report.totalAnnualLeaveDays, 10) || 0) +
      (parseInt(report.totalWeeklyLeaveDays, 10) || 0) +
      (parseInt(report.totalMedicalLeaveDays, 10) || 0) +
      (parseInt(report.officialLeaveDays, 10) || 0);

    let updatedReport = { ...report };

    if (totalDays !== 30) {
      const daysDiff = 30 - totalDays;
      updatedReport.totalWeeklyLeaveDays = (parseInt(report.totalWeeklyLeaveDays, 10) || 0) + daysDiff;
    }

    const baseMealAllowance = 500;
    const absenceDays = parseInt(report.totalAbsenceDays, 10) || 0;
    updatedReport.mealAllowance = Math.max(0, baseMealAllowance - absenceDays * 50).toFixed(2);

    const dailySalary = parseFloat(report.baseSalary) / 30;
    const hourlyRate = dailySalary / 9;

    const medicalLeaveDays = parseInt(report.totalMedicalLeaveDays, 10) || 0;
    updatedReport.medicalLeaveCompensation = (medicalLeaveDays * dailySalary).toFixed(2);

    const officialLeaveDays = parseInt(report.officialLeaveDays, 10) || 0;
    updatedReport.leaveCompensation = (officialLeaveDays * dailySalary * 2).toFixed(2);

    const lateDeductionDays = parseFloat(report.lateDeductionDays) || 0;
    const totalDeductions = absenceDays + lateDeductionDays;
    updatedReport.totalDeductions = totalDeductions.toFixed(2);
    updatedReport.lateDeductionDays = lateDeductionDays.toFixed(2);

    const penaltiesValue = parseFloat(report.penaltiesValue) || 0;
    const violationsInstallment = parseFloat(report.violationsInstallment) || 0;
    const totalViolationsValue = parseFloat(report.totalViolationsValue) || 0;
    updatedReport.deductionsValue = (totalDeductions * dailySalary + totalViolationsValue).toFixed(2);
    updatedReport.penaltiesValue = penaltiesValue.toFixed(2);
    updatedReport.violationsInstallment = violationsInstallment.toFixed(2);
    updatedReport.totalViolationsValue = totalViolationsValue.toFixed(2);
    updatedReport.remainingViolationsBalance = Math.max(0, totalViolationsValue - violationsInstallment).toFixed(2);

    const overtimeValue = (parseFloat(report.totalOvertime) || 0) * hourlyRate;
    updatedReport.overtimeValue = overtimeValue.toFixed(2);

    const advancePayment = parseFloat(report.advancePayment) || 0;
    updatedReport.advancePayment = advancePayment.toFixed(2);

    updatedReport.netSalary = (
      parseFloat(report.baseSalary) +
      parseFloat(updatedReport.mealAllowance) +
      parseFloat(updatedReport.medicalLeaveCompensation) +
      parseFloat(updatedReport.leaveCompensation) +
      overtimeValue +
      parseFloat(report.eidBonus || 0) -
      parseFloat(report.medicalInsurance) -
      parseFloat(report.socialInsurance) -
      parseFloat(updatedReport.deductionsValue) -
      advancePayment
    ).toFixed(2);

    return updatedReport;
  };

  const handleEditClick = (report) => {
    setEditingReport(report);
    setEditForm({
      code: report.code,
      fullName: report.fullName,
      department: report.department,
      baseSalary: report.baseSalary,
      medicalInsurance: report.medicalInsurance,
      socialInsurance: report.socialInsurance,
      mealAllowance: report.mealAllowance,
      totalWorkHours: report.totalWorkHours,
      totalWorkDays: report.totalWorkDays,
      totalAbsenceDays: report.totalAbsenceDays,
      lateDeductionDays: report.lateDeductionDays || '0.00',
      totalDeductions: report.totalDeductions,
      deductionsValue: report.deductionsValue || '0.00',
      totalOvertime: report.totalOvertime,
      overtimeValue: report.overtimeValue,
      totalWeeklyLeaveDays: report.totalWeeklyLeaveDays,
      totalAnnualLeaveDays: report.totalAnnualLeaveDays,
      totalMedicalLeaveDays: report.totalMedicalLeaveDays,
      totalAnnualLeaveYear: report.totalAnnualLeaveYear,
      penaltiesValue: report.penaltiesValue || '0.00',
      violationsInstallment: report.violationsInstallment || '0.00',
      totalViolationsValue: report.totalViolationsValue || '0.00',
      remainingViolationsBalance: report.remainingViolationsBalance || '0.00',
      netSalary: report.netSalary,
      eidBonus: report.eidBonus || '0.00',
      medicalLeaveCompensation: report.medicalLeaveCompensation || '0.00',
      officialLeaveDays: report.officialLeaveDays || '0',
      leaveCompensation: report.leaveCompensation || '0.00',
      advancePayment: report.advancePayment || '0.00',
    });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const validations = [
        { field: 'baseSalary', label: 'الراتب الأساسي' },
        { field: 'medicalInsurance', label: 'التأمين الطبي' },
        { field: 'socialInsurance', label: 'التأمين الاجتماعي' },
        { field: 'totalOvertime', label: 'الساعات الإضافية' },
        { field: 'totalAbsenceDays', label: 'أيام الغياب' },
        { field: 'totalAnnualLeaveDays', label: 'أيام الإجازة السنوية (الفترة)' },
        { field: 'totalMedicalLeaveDays', label: 'أيام الإجازة الطبية' },
        { field: 'totalWeeklyLeaveDays', label: 'أيام الإجازة الأسبوعية' },
        { field: 'eidBonus', label: 'العيدية' },
        { field: 'penaltiesValue', label: 'قيمة الجزاءات' },
        { field: 'violationsInstallment', label: 'قسط المخالفات' },
        { field: 'totalViolationsValue', label: 'إجمالي قيمة المخالفات' },
        { field: 'officialLeaveDays', label: 'أيام الإجازة الرسمية' },
        { field: 'advancePayment', label: 'السلف' },
      ];

      for (const { field, label } of validations) {
        if (parseFloat(editForm[field]) < 0) {
          setError(`${label} لا يمكن أن يكون سالبًا`);
          setLoading(false);
          return;
        }
      }

      const totalDays =
        (parseInt(editForm.totalWorkDays, 10) || 0) +
        (parseInt(editForm.totalAbsenceDays, 10) || 0) +
        (parseInt(editForm.totalAnnualLeaveDays, 10) || 0) +
        (parseInt(editForm.totalMedicalLeaveDays, 10) || 0) +
        (parseInt(editForm.totalWeeklyLeaveDays, 10) || 0) +
        (parseInt(editForm.officialLeaveDays, 10) || 0);

      if (totalDays !== 30) {
        setError(`إجمالي الأيام يجب أن يكون 30 يومًا، الحالي: ${totalDays}`);
        setLoading(false);
        return;
      }

      const prevAbsenceDays = parseInt(editingReport.totalAbsenceDays, 10) || 0;
      const newAbsenceDays = parseInt(editForm.totalAbsenceDays, 10) || 0;
      const prevAnnualLeaveDays = parseInt(editingReport.totalAnnualLeaveDays, 10) || 0;
      const newAnnualLeaveDays = parseInt(editForm.totalAnnualLeaveDays, 10) || 0;
      const prevMedicalLeaveDays = parseInt(editingReport.totalMedicalLeaveDays, 10) || 0;
      const newMedicalLeaveDays = parseInt(editForm.totalMedicalLeaveDays, 10) || 0;
      const annualLeaveDaysDiff = newAnnualLeaveDays - prevAnnualLeaveDays;

      const dailySalary = parseFloat(editForm.baseSalary) / 30;
      const hourlyRate = dailySalary / 9;
      const overtimeValue = (parseFloat(editForm.totalOvertime) || 0) * hourlyRate;

      const updatedMedicalLeaveCompensation = (newMedicalLeaveDays * dailySalary).toFixed(2);
      const updatedLeaveCompensation = (parseInt(editForm.officialLeaveDays, 10) || 0) * dailySalary * 2;

      const lateDeductionDays = parseFloat(editForm.lateDeductionDays) || 0;
      const updatedTotalDeductions = newAbsenceDays + lateDeductionDays;

      const penaltiesValue = parseFloat(editForm.penaltiesValue) || 0;
      const violationsInstallment = parseFloat(editForm.violationsInstallment) || 0;
      const totalViolationsValue = parseFloat(editForm.totalViolationsValue) || 0;
      const updatedRemainingViolationsBalance = Math.max(0, totalViolationsValue - violationsInstallment).toFixed(2);

      const updatedDeductionsValue = (updatedTotalDeductions * dailySalary + totalViolationsValue).toFixed(2);

      const baseMealAllowance = 500;
      const updatedMealAllowance = Math.max(0, baseMealAllowance - newAbsenceDays * 50).toFixed(2);

      const advancePayment = parseFloat(editForm.advancePayment) || 0;

      let updatedWeeklyLeaveDays = parseInt(editForm.totalWeeklyLeaveDays, 10) || 0;
      if (totalDays !== 30) {
        updatedWeeklyLeaveDays += 30 - totalDays;
      }

      const updatedNetSalary = (
        parseFloat(editForm.baseSalary) +
        parseFloat(updatedMealAllowance) +
        parseFloat(updatedMedicalLeaveCompensation) +
        parseFloat(updatedLeaveCompensation) +
        overtimeValue +
        parseFloat(editForm.eidBonus || 0) -
        parseFloat(editForm.medicalInsurance) -
        parseFloat(editForm.socialInsurance) -
        parseFloat(updatedDeductionsValue) -
        advancePayment
      ).toFixed(2);

      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/users/${editForm.code}`,
        {
          code: editForm.code,
          fullName: editForm.fullName,
          department: editForm.department,
          baseSalary: parseFloat(editForm.baseSalary),
          medicalInsurance: parseFloat(editForm.medicalInsurance),
          socialInsurance: parseFloat(editForm.socialInsurance),
          mealAllowance: parseFloat(updatedMealAllowance),
          deductionsValue: parseFloat(updatedDeductionsValue),
          penaltiesValue: parseFloat(editForm.penaltiesValue) || 0,
          violationsInstallment: parseFloat(editForm.violationsInstallment) || 0,
          totalViolationsValue: parseFloat(editForm.totalViolationsValue) || 0,
          remainingViolationsBalance: parseFloat(updatedRemainingViolationsBalance),
          totalAnnualLeave: parseFloat(editForm.totalAnnualLeaveYear) + annualLeaveDaysDiff,
          createdBy: user._id,
          eidBonus: parseFloat(editForm.eidBonus) || 0,
          medicalLeaveCompensation: parseFloat(updatedMedicalLeaveCompensation),
          officialLeaveDays: parseInt(editForm.officialLeaveDays, 10) || 0,
          leaveCompensation: parseFloat(updatedLeaveCompensation),
          advancePayment: parseFloat(editForm.advancePayment) || 0,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );

      setSalaryReports((prev) =>
        prev.map((report) =>
          report.code === editForm.code
            ? {
                ...report,
                ...editForm,
                mealAllowance: updatedMealAllowance,
                totalDeductions: updatedTotalDeductions.toFixed(2),
                deductionsValue: updatedDeductionsValue,
                overtimeValue: overtimeValue.toFixed(2),
                lateDeductionDays: lateDeductionDays.toFixed(2),
                penaltiesValue: penaltiesValue.toFixed(2),
                violationsInstallment: violationsInstallment.toFixed(2),
                totalViolationsValue: totalViolationsValue.toFixed(2),
                remainingViolationsBalance: updatedRemainingViolationsBalance,
                netSalary: updatedNetSalary,
                totalWeeklyLeaveDays: updatedWeeklyLeaveDays,
                totalAnnualLeaveYear: parseFloat(editForm.totalAnnualLeaveYear) + annualLeaveDaysDiff,
                eidBonus: parseFloat(editForm.eidBonus) || 0,
                medicalLeaveCompensation: updatedMedicalLeaveCompensation,
                leaveCompensation: updatedLeaveCompensation.toFixed(2),
                advancePayment: advancePayment.toFixed(2),
              }
            : report
        )
      );

      setEditingReport(null);
      alert('تم حفظ التعديلات بنجاح');
    } catch (err) {
      console.error('Error updating report:', err.response?.data?.message || err.message);
      setError(`خطأ أثناء التعديل: ${err.response?.data?.message || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEditCancel = () => {
    setEditingReport(null);
    setEditForm({});
    setError('');
  };

  const toggleColumn = (column) => {
    setVisibleColumns((prev) => ({ ...prev, [column]: !prev[column] }));
  };

  if (!user || user.role !== 'admin') return null;

  return (
    <div className="min-h-screen bg-gray-100">
      <NavBar />
      <div className="container mx-auto p-4 sm:p-6">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="bg-white p-4 sm:p-6 rounded-xl shadow-md border border-gray-100 mb-6"
        >
          <h2 className="text-lg sm:text-xl font-semibold text-gray-700 mb-4 text-right">البحث في تقرير المرتب الشهري</h2>
          {error && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="bg-red-100 text-red-700 p-3 rounded-md mb-4 text-right text-sm"
            >
              {error}
            </motion.div>
          )}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 sm:gap-4">
            <div>
              <label className="block text-gray-700 text-sm font-medium mb-1 text-right">كود الموظف</label>
              <input
                type="text"
                value={searchCode}
                onChange={(e) => setSearchCode(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
                placeholder="أدخل كود الموظف"
              />
            </div>
            <div>
              <label className="block text-gray-700 text-sm font-medium mb-1 text-right">من تاريخ</label>
              <input
                type="date"
                value={dateFrom}
                onChange={(e) => setDateFrom(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
              />
            </div>
            <div>
              <label className="block text-gray-700 text-sm font-medium mb-1 text-right">إلى تاريخ</label>
              <input
                type="date"
                value={dateTo}
                onChange={(e) => setDateTo(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
              />
            </div>
          </div>
          <div className="flex flex-wrap justify-end gap-2 sm:gap-4 mt-4">
            <motion.button
              onClick={handleSearch}
              disabled={loading}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`w-full sm:w-auto bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors duration-300 text-sm ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              {loading ? 'جارٍ البحث...' : 'بحث'}
            </motion.button>
            <motion.button
              onClick={handleShowAll}
              disabled={loading}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`w-full sm:w-auto bg-purple-600 text-white px-4 py-2 rounded-md hover:bg-purple-700 transition-colors duration-300 text-sm ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              {loading ? 'جارٍ الجلب...' : 'عرض الكل'}
            </motion.button>
            <motion.button
              onClick={handleExportToExcel}
              disabled={loading || salaryReports.length === 0}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`w-full sm:w-auto bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition-colors duration-300 text-sm ${loading || salaryReports.length === 0 ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              تصدير إلى Excel
            </motion.button>
            <motion.button
              onClick={handleExportToWord}
              disabled={loading || salaryReports.length === 0}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`w-full sm:w-auto bg-blue-800 text-white px-4 py-2 rounded-md hover:bg-blue-900 transition-colors duration-300 text-sm ${loading || salaryReports.length === 0 ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              تصدير إلى Word
            </motion.button>
          </div>
        </motion.div>

        <AnimatePresence>
          {editingReport && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
            >
              <motion.div
                className="bg-white p-4 sm:p-6 rounded-xl shadow-lg w-full max-w-4xl overflow-y-auto max-h-[90vh]"
                onClick={(e) => e.stopPropagation()}
              >
                <h2 className="text-lg sm:text-xl font-semibold text-gray-700 mb-4 text-right">تعديل تقرير المرتب</h2>
                {error && (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="bg-red-100 text-red-700 p-3 rounded-md mb-4 text-right text-sm"
                  >
                    {error}
                  </motion.div>
                )}
                <form onSubmit={handleEditSubmit} className="space-y-6">
                  <div className="border-b pb-4">
                    <h3 className="text-md font-semibold text-gray-600 mb-3 text-right">معلومات الموظف</h3>
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">كود الموظف</label>
                        <input
                          type="text"
                          name="code"
                          value={editForm.code}
                          onChange={handleEditChange}
                          className="w-full px-3 py-2 border rounded-lg text-right bg-gray-100 cursor-not-allowed text-sm"
                          readOnly
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">الاسم الكامل</label>
                        <input
                          type="text"
                          name="fullName"
                          value={editForm.fullName}
                          onChange={handleEditChange}
                          className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">القسم</label>
                        <input
                          type="text"
                          name="department"
                          value={editForm.department}
                          onChange={handleEditChange}
                          className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
                          required
                        />
                      </div>
                    </div>
                  </div>
                  <div className="border-b pb-4">
                    <h3 className="text-md font-semibold text-gray-600 mb-3 text-right">الراتب والمخصصات</h3>
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">الراتب الأساسي</label>
                        <input
                          type="number"
                          name="baseSalary"
                          value={editForm.baseSalary}
                          onChange={handleEditChange}
                          className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
                          required
                          min="0"
                          step="0.01"
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">التأمين الطبي</label>
                        <input
                          type="number"
                          name="medicalInsurance"
                          value={editForm.medicalInsurance}
                          onChange={handleEditChange}
                          className="w-full px-3 py-2 border rounded-lg text-right focus:outline-none focus:ring-2 focus:ring-blue-600 text-sm"
                          min="0"
                          step="0.01"
                        />
                      </div>
                      <div>
                        <label className="block text-gray-700 text-sm font-medium mb-1 text-right">التأمين الاجتماعي</label>
                        <input
                          type="number"
                          name="socialInsurance"
                          value={editForm.socialInsurance}
                          onChange={handleEditChange}
