  function handleExportToExcel() {
    const headers = [
      { key: 'code', label: 'كود الموظف', visible: visibleColumns.code },
      { key: 'fullName', label: 'الاسم الكامل', visible: visibleColumns.fullName },
      { key: 'department', label: 'القسم', visible: visibleColumns.department },
      { key: 'baseSalary', label: 'الراتب الأساسي', visible: visibleColumns.baseSalary },
      { key: 'medicalInsurance', label: 'التأمين الطبي', visible: visibleColumns.medicalInsurance },
      { key: 'socialInsurance', label: 'التأمين الاجتماعي', visible: visibleColumns.socialInsurance },
      { key: 'mealAllowance', label: 'بدل الوجبة', visible: visibleColumns.mealAllowance },
      { key: 'eidBonus', label: 'العيدية', visible: visibleColumns.eidBonus },
      { key: 'totalWorkHours', label: 'إجمالي ساعات العمل', visible: visibleColumns.totalWorkHours },
      { key: 'totalWorkDays', label: 'إجمالي أيام العمل', visible: visibleColumns.totalWorkDays },
      { key: 'totalAbsenceDays', label: 'إجمالي أيام الغياب', visible: visibleColumns.totalAbsenceDays },
      { key: 'lateDeductionDays', label: 'خصم التأخير (أيام)', visible: visibleColumns.lateDeductionDays },
      { key: 'totalDeductions', label: 'إجمالي الخصومات (أيام)', visible: visibleColumns.totalDeductions },
      { key: 'deductionsValue', label: 'قيمة الخصومات', visible: visibleColumns.deductionsValue },
      { key: 'totalOvertime', label: 'إجمالي الساعات الإضافية', visible: visibleColumns.totalOvertime },
      { key: 'overtimeValue', label: 'قيمة الساعات الإضافية', visible: visibleColumns.overtimeValue },
      { key: 'totalWeeklyLeaveDays', label: 'إجمالي أيام الإجازة الأسبوعية', visible: visibleColumns.totalWeeklyLeaveDays },
      { key: 'totalAnnualLeaveDays', label: 'إجمالي أيام الإجازة السنوية (الفترة)', visible: visibleColumns.totalAnnualLeaveDays },
      { key: 'totalMedicalLeaveDays', label: 'إجمالي أيام الإجازة الطبية', visible: visibleColumns.totalMedicalLeaveDays },
      { key: 'totalAnnualLeaveYear', label: 'إجمالي أيام الإجازة السنوية (السنة)', visible: visibleColumns.totalAnnualLeaveYear },
      { key: 'penaltiesValue', label: 'قيمة الجزاءات', visible: visibleColumns.penaltiesValue },
      { key: 'violationsInstallment', label: 'قسط المخالفات', visible: visibleColumns.violationsInstallment },
      { key: 'totalViolationsValue', label: 'إجمالي قيمة المخالفات', visible: visibleColumns.totalViolationsValue },
      { key: 'remainingViolationsBalance', label: 'رصيد المخالفات المتبقي', visible: visibleColumns.remainingViolationsBalance },
      { key: 'officialLeaveDays', label: 'إجمالي أيام الإجازة الرسمية', visible: visibleColumns.officialLeaveDays },
      { key: 'leaveCompensation', label: 'بدل الإجازة الرسمية', visible: visibleColumns.leaveCompensation },
      { key: 'advancePayment', label: 'السلف', visible: visibleColumns.advancePayment },
      { key: 'netSalary', label: 'الراتب الصافي', visible: visibleColumns.netSalary },
    ];

    const data = salaryReports.map((report) =>
      headers.reduce((obj, header) => {
        if (header.visible) {
          obj[header.label] = report[header.key];
        }
        return obj;
      }, {})
    );

    const totals = headers.reduce((obj, header) => {
      if (header.visible) {
        if (['code', 'fullName', 'department'].includes(header.key)) {
          obj[header.label] = 'الإجمالي';
        } else {
          obj[header.label] = salaryReports
            .reduce((sum, report) => sum + parseFloat(report[header.key] || 0), 0)
            .toFixed(2);
        }
      }
      return obj;
    }, {});

    const ws = XLSX.utils.json_to_sheet([...data, totals], {
      header: headers.filter((h) => h.visible).map((h) => h.label),
      skipHeader: false,
    });

    ws['!cols'] = headers.filter((h) => h.visible).map(() => ({ wch: 20 }));
    ws['!dir'] = { rtl: true };

    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'تقرير المرتبات الشهرية');
    XLSX.write(wb, 'Monthly_Salary_Report.xlsx');
  }

