      { key: 'totalAnnualLeaveDays', label: 'إجمالي أيام الإجازة السنوية (الفترة)', visible: visibleColumns.totalAnnualLeaveDays },
      { key: 'totalMedicalLeaveDays', label: 'إجمالي أيام الإجازة الطبية', visible: visibleColumns.totalMedicalLeaveDays },
      { key: 'totalAnnualLeaveYear', label: 'إجمالي أيام الإجازة السنوية (السنة)', visible: visibleColumns.totalAnnualLeaveYear },
      { key: 'penaltiesValue', label: 'قيمة الجزاءات', visible: visibleColumns.penaltiesValue },
      { key: 'violationsInstallment', label: 'قسط المخالفات', visible: visibleColumns.violationsInstallment },
      { key: 'totalViolationsValue', label: 'إجمالي قيمة المخالفات', visible: visibleColumns.totalViolationsValue },
      { key: 'remainingViolationsBalance', label: 'رصيد المخالفات المتبقي', visible: visibleColumns.remainingViolationsBalance },
      { key: 'officialLeaveDays', label: 'إجمالي أيام الإجازة الرسمية', visible: visibleColumns.officialLeaveDays },
      { key: 'leaveCompensation', label: 'بدل الإجازة الرسمية', visible: visibleColumns.leaveCompensation },
      { key: 'advancePayment', label: 'السلف', visible: visibleColumns.advancePayment },
      { key: 'netSalary', label: 'الراتب الصافي', visible: visibleColumns.netSalary },
    ];

    const tableRows = [
      new TableRow({
        children: headers
          .filter((h) => h.visible)
          .map(
            (header) =>
              new TableCell({
                children: [new Paragraph({ text: header.label, alignment: 'right', bold: true })],
                width: { size: 100 / headers.filter((h) => h.visible).length, type: WidthType.PERCENTAGE },
              })
          )
          .concat(
            new TableCell({
              children: [new Paragraph({ text: 'الإجراءات', alignment: 'right', bold: true })],
              width: { size: 100 / (headers.filter((h) => h.visible).length + 1), type: WidthType.PERCENTAGE },
            })
          ),
      }),
      ...salaryReports.map(
        (report) =>
          new TableRow({
            children: headers
              .filter((h) => h.visible)
              .map(
                (header) =>
                  new TableCell({
                    children: [
                      new Paragraph({
                        text: report[header.key] ? report[header.key].toString() : '0',
                        alignment: 'right',
                      }),
                    ],
                    width: { size: 100 / headers.filter((h) => h.visible).length, type: WidthType.PERCENTAGE },
                  })
              )
              .concat(
                new TableCell({
                  children: [new Paragraph({ text: '', alignment: 'right' })],
                  width: { size: 100 / (headers.filter((h) => h.visible).length + 1), type: WidthType.PERCENTAGE },
                })
              ),
          })
      ),
      new TableRow({
        children: headers
          .filter((h) => h.visible)
          .map(
            (header) =>
              new TableCell({
                children: [
                  new Paragraph({
                    text:
                      ['code', 'fullName', 'department'].includes(header.key)
                        ? 'الإجمالي'
                        : salaryReports
                            .reduce((sum, report) => sum + parseFloat(report[header.key] || 0), 0)
                            .toFixed(2),
                    alignment: 'right',
                    bold: true,
                  }),
                ],
                width: { size: 100 / headers.filter((h) => h.visible).length, type: WidthType.PERCENTAGE },
              })
          )
          .concat(
            new TableCell({
              children: [new Paragraph({ text: '', alignment: 'right' })],
              width: { size: 100 / (headers.filter((h) => h.visible).length + 1), type: WidthType.PERCENTAGE },
            })
          ),
      }),
    ];

    const table = new Table({
      rows: tableRows,
      width: { size: 100, type: WidthType.PERCENTAGE },
    });

    const doc = new Document({
      sections: [
        {
          properties: {},
          children: [
            new Paragraph({
              text: 'تقرير المرتبات الشهرية',
              heading: 'Heading1',
              alignment: 'center',
            }),
            new Paragraph({ text: '' }),
            table,
          ],
        },
      ],
    });

    Packer.toBlob(doc).then((blob) => {
      saveAs(blob, 'Monthly_Salary_Report.docx');
    });
  }
